#if 

#define MACRO(s, elem) MACRO_I((), elem)
#define MACRO_I(_, elem) \
     _((elem))( \
         _( \
            MACRO_ID _(), () elem \
        ), \
        { elem } \
    ) \
    /**/
#define MACRO_ID() MACRO

(
    MACRO, () (a, b, (c, d), e, (f, (g, h)))
)
// { a } { b } { c } { d } { e } { f } { g } { h }

