((
    ((1)),
    (A)(B)
))
// (a, x) (a, y) (b, x) (b, y)

#endif

#define X () a b c
#define Y () (+)(-)
#define Z () (?, ...)

((
    MACRO, (X)(Y)(Z), ~
))
// (a, +, ?) (a, -, ?)
// (b, +, ?) (b, -, ?)
// (c, +, ?) (c, -, ?)
