#define ADD(...) \
    (ADD_I( \
        (), (()), \
        (__VA_ARGS__) \
    )) \
    /**/
#define ADD_INDIRECT() ADD_I
#define ADD_I(_, s, tuple) \
     _ \
        (0xIIF)((tuple))( \
             _( \
                s,  _(tuple), \
                (s) _(ADD_INDIRECT _()( \
                     _(), (s), \
                     _(tuple) \
                )) \
            ) \
        ) \
        (0xELSE)(0) \
        (0xENDIF) \
    /**/

