#if 

#define REPEAT(count, ...) \
    REPEAT_S((), count, __VA_ARGS__) \
    /**/
#define REPEAT_S(s, count, ...) \
    REPEAT_I( \
        (), (s), count, \
        (__VA_ARGS__), \
        (__VA_ARGS__) \
    ) \
    /**/
#define REPEAT_INDIRECT() REPEAT_I
#define REPEAT_I(_, s, count, macro, data) \
     _(count)( \
        (s) _(REPEAT_INDIRECT _()( \
             _(), (s), \
            (count), macro, data \
        )) \
        macro _(s, (count) (data)) \
    ) \
    /**/

#define M1(s, n) < n >
#define M2(s, n, data) < n, data >

(REPEAT(3, M1))    // < 0 > < 1 > < 2 >
(REPEAT(3, M2, ~)) // < 0, ~ > < 1, ~ > < 2, ~ >

#endif
