#if 

#define REPEAT(count, macro, ...) \
    REPEAT_S((), count, macro, __VA_ARGS__) \
    /**/
#define REPEAT_S(s, count, macro, ...) \
    REPEAT_I( \
        (), (s), count, \
        macro, (macro), (__VA_ARGS__) \
    ) \
    /**/

#else /*  */

#define REPEAT(count, macro, data) \
    REPEAT_S((), count, macro, data) \
    /**/
#define REPEAT_S(s, count, macro, data) \
    REPEAT_I( \
        (), (s), count, \
        macro, (macro), (data) \
    ) \
    /**/

#endif /*  */

#define REPEAT_INDIRECT() REPEAT_I
#define REPEAT_I(_, s, count, macro, _m, pd) \
     _(count)( \
        (s) _(REPEAT_INDIRECT _()( \
             _(), (s), \
            (count), macro, _m, pd \
        )) \
        _m()( \
            s, macro, (count) \
            ()  pd \
        ) \
    ) \
    /**/

#define MACRO(s, n, id) (n) id ## n

(REPEAT(3, MACRO, class T))
// class T0, class T1, class T2

#if 

