macro.listing:
#include 
#include 

#define A(a, b, c) { a, b, c }
#define A_ID() A

#define B(a, b) { a, b }
#define B_ID() B

#define X(cl) Y(cl(x))
#define Y(cl) Z(cl(y))
#define Z(cl) ((cl(z)))

X((A_ID, 3)) // { x, y, z }
X((B_ID, 2)) // { x, y }

#include 
#include 

#define MACRO(a, b) int[a][b]

#define X(cl) Y(cl(x))
#define Y(cl) ((cl(y)))

X((MACRO, 2)) // int[x][y]

#include 
#include 
#include 

#define A(a) { a }
#define B(a, b) { a + b }

#define X(cl) (Y(cl(1)))
#define Y(cl) \
    ((cl))( \
        (cl), \
        Z(cl(2)) \
    ) \
    /**/
#define Z(cl) (cl)

X((A, 1)) // { 1 }
X((B, 2)) // { 1 + 2 }

#include 
#include 

#define MACRO(a, b) #a #b

#define I \
    ( \
        (MACRO, 2)(x)(y) \
    ) \
    /**/

I                // MACRO(x, y)
(I) // "x" "y"
