#define USER_1(x) x
#define USER_2(x) x
#define USER_3(x) x
#define USER_4(x) x
#define USER_5(x) x
#define USER_6(x) x
#define USER_7(x) x
#define USER_8(x) x

#define STATE() (STATE_P, 8)
#define STATE_P(n) (USER_ ## n(()))

#define USER USER_S(STATE())
#define USER_S(s) (USER_, s)

#define REPEAT(count, macro, data) \
    REPEAT_S(STATE(), count, macro, data) \
    /**/
#define REPEAT_S(s, count, macro, data) \
    REPEAT_I( \
        (), (s), \
        count, macro, data \
    ) \
    /**/
#define REPEAT_INDIRECT() REPEAT_I
#define REPEAT_I(_, s, count, macro, data) \
     _(count)( \
        USER_S(s) _(REPEAT_INDIRECT _()( \
             _(), (s), \
            (count), macro, data \
        )) \
        macro _(s, (count), data) \
    ) \
    /**/

#define FIXED(s, n, text) (n) text
#define TTP(s, n, prefix) \
    (n) \
    template< \
        USER(REPEAT((n), FIXED, class)) \
    > class prefix ## n \
    /**/

USER(REPEAT(3, TTP, T))
// template<class> class T0,
// template<class, class> class T1,
// template<class, class, class> class T2
