#define AUTO_REP (AUTO_REP_, ())

#define AUTO_REP_1(...) (1)(, (REP(__VA_ARGS__)))
#define AUTO_REP_2(...) (2)(, (REP(__VA_ARGS__)))
#define AUTO_REP_3(...) (3)(, (REP(__VA_ARGS__)))
#define AUTO_REP_4(...) (4)(, (REP(__VA_ARGS__)))
#define AUTO_REP_5(...) (5)(, (REP(__VA_ARGS__)))
#define AUTO_REP_6(...) (6)(, (REP(__VA_ARGS__)))
#define AUTO_REP_7(...) (7)(, (REP(__VA_ARGS__)))
#define AUTO_REP_8(...) (8)(, (REP(__VA_ARGS__)))

#define TEXT(s, n, text) (n) text
#define TTP(s, n, id) \
    (n) \
    template<AUTO_REP((n), TEXT, class)> class id ## n \
    /**/

AUTO_REP(3, TTP, T)
