#include 
#include 

#define ENUM(seq) \
    ( \
        ENUM_A (seq, ~) \
    ) \
    /**/

#if 

#define ENUM_A(bit, ...) \
    (bit)(__VA_ARGS__ ENUM_INDIRECT) \
    /**/
#define ENUM_B(bit, ...) \
    (bit) ENUM_A(bit, __VA_ARGS__) \
    /**/

#else

#define ENUM_A(bit, x) (bit)(x ENUM_INDIRECT)
#define ENUM_B(bit, x) (bit) ENUM_A(bit, x)

#endif

#define ENUM_INDIRECT() ENUM_B

ENUM((a)(b)(c)) // a, b, c

#if 

ENUM(
    (std::pair<int, int>)
    (std::basic_string<char, std::char_traits<char> >)
    (std::vector<double>)
)
// std::pair<int, int>,
// std::basic_string<char, std::char_traits<char> >,
// std::vector<double>

#endif
