#define T_MINUS(n) T_MINUS_BYPASS(, n)
#define T_MINUS_BYPASS(s, n) \
    (s)(T_MINUS_I( \
        (s), n \
    )) \
    /**/
#define T_MINUS_INDIRECT() T_MINUS_I
#define T_MINUS_I(s, n) \
    n (n)( \
        T_MINUS_II, ?! (3) \
    )((), s, n) \
    /**/
#define T_MINUS_II(_, s, n) \
    (s) _(T_MINUS_INDIRECT _()( \
        (s), (n) \
    )) \
    /**/

