#define A(x, y) \
    ( \
        (B)( \
            (2) x, \
            (2) y \
        ) \
    ) \
    /**/
#define B(a, b, c, d) a + b + c + d

A((1, 2), (3, 4)) // 1 + 2 + 3 + 4

