#if 

#define REPEAT(count, macro, ...) \
    REPEAT_S((), count, macro, __VA_ARGS__) \
    /**/
#define REPEAT_S(s, count, macro, ...) \
    REPEAT_I( \
        (), (s), \
        count, macro, (__VA_ARGS__) \
    ) \
    /**/

#else

#define REPEAT(count, macro, data) \
    REPEAT_S((), count, macro, data) \
    /**/
#define REPEAT_S(s, count, macro, data) \
    REPEAT_I( \
        (), (s), \
        count, macro, (data) \
    ) \
    /**/

#endif

#define REPEAT_INDIRECT() REPEAT_I
#define REPEAT_I(_, s, count, macro, pd) \
     _(count)( \
        (s) _(REPEAT_INDIRECT _()( \
             _(), (s), \
            (count), macro, pd \
        )) \
        macro _(s, (count),  pd) \
    ) \
    /**/

#if 

#define MACRO(s, n, x, y) (n) class x ## n = y

(REPEAT(
    3, MACRO, T, U
))
// class T0 = U, class T1 = U, class T2 = U

#else

#define MACRO(s, n, xy) \
    (n) \
    class ((0) xy, n) \
        = (1) xy \
    /**/

(REPEAT(
    3, MACRO, (T, U)
))
// class T0 = U, class T1 = U, class T2 = U

#endif
